var customerAuthGuard = (route, state) => {
  const customerAuth = inject(CustomerAuthService);
  const router = inject(Router);
  return customerAuth.getNullableUser$().pipe(map((user) => {
    if (user) {
      return true;
    } else {
      router.navigate(["/store/login"]);
      return false;
    }
  }));
};

