// src/app/modules/store/guards/admin-auth.guard.ts
var adminAuthGuard = (route, state) => {
  const adminAuth = inject(AdminAuthService);
  const router = inject(Router);
  return adminAuth.getNullableUser$().pipe(map((user) => {
    if (!user) {
      router.navigate(["/store/admin/login"]);
      return false;
    }
    let roleId = 0;
    if (typeof user.role === "object" && user.role !== null) {
      roleId = user.role.roleId || 0;
    } else if (typeof user.role === "number") {
      roleId = user.role;
    }
    if (roleId >= 300) {
      console.log("[adminAuthGuard] \uAD00\uB9AC\uC790 \uAD8C\uD55C \uD655\uC778 (roleId:", roleId, ")");
      return true;
    }
    console.log("[adminAuthGuard] \uAD00\uB9AC\uC790 \uAD8C\uD55C \uC5C6\uC74C (roleId:", roleId, ")");
    router.navigate(["/store"]);
    return false;
  }));
};

