// src/app/modules/education/guards/admin-auth.guard.ts
var adminAuthGuard = (route, state) => {
  const adminAuth = inject(AdminAuthService);
  const router = inject(Router);
  const isAuthenticated = adminAuth.isAuthenticated();
  if (!isAuthenticated) {
    console.warn("[adminAuthGuard] \uC778\uC99D \uC2E4\uD328 - \uB85C\uADF8\uC778 \uD398\uC774\uC9C0\uB85C \uC774\uB3D9");
    router.navigate(["/education/admin/login"]);
    return false;
  }
  const user = adminAuth.getCurrentUser();
  let roleId = 0;
  if (typeof user?.role === "object" && user?.role !== null) {
    roleId = user.role.roleId || 0;
  } else if (typeof user?.role === "number") {
    roleId = user.role;
  }
  if (roleId >= 300) {
    console.log("[adminAuthGuard] \uAD00\uB9AC\uC790 \uAD8C\uD55C \uD655\uC778 (roleId:", roleId, ")");
    return true;
  }
  console.log("[adminAuthGuard] \uAD00\uB9AC\uC790 \uAD8C\uD55C \uC5C6\uC74C (roleId:", roleId, ")");
  router.navigate(["/education"]);
  return false;
};

